int n;
int dfs(int l1,int r1,int l2,int r2){
    if(dp[l1][r1][l2][r2]!=-1)return dp[l1][r1][l2][r2];
    int ans=max((l2-l1+1),(r2-r1+1));
    for(int i = l1;i<l2;i++)ans=min(ans,dfs(l1,r1,i,r2)+dfs(i+1,r1,l2,r2));
    for(int i = r1;i<r2;i++)ans=min(ans,dfs(l1,r1,l2,i)+dfs(l1,i+1,l2,r2));
    return dp[l1][r1][l2][r2]=ans;
}

int main(){
    memset(dp,-1,sizeof(dp));
    n=read();
    for(int i = 1;i<=n;i++){
        for(int j = 1;j<=n;j++){
            char c;
            cin>>c;
            g[i][j]=(c=='.');
            dp[i][j][i][j]=(c=='#');    
        }
    }
    cout<<dfs(1,1,n,n);
    return 0;
}